class Solution {
public:
    string minRemoveToMakeValid(string s) {
        int C = 0; string ans; vector<char> hehe;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '(') { C++; hehe.push_back(s[i]); } 
            else if (s[i] == ')') {if (C > 0) { C--; hehe.push_back(s[i]); }}
            else hehe.push_back(s[i]); 
        }
        if (C > 0) for (int i = hehe.size() - 1; i >= 0; i--) if (hehe[i] == '(') { hehe.erase(hehe.begin() + i); C--; if (C == 0) break; }
