class Solution {
    public String minRemoveToMakeValid(String s) {
        Stack<Pair<Character,Integer>> validStack = new Stack<>();
        for(int i = 0; i < s.length(); i++){
            if(s.charAt(i) == '(' || s.charAt(i) == ')'){
                char top = s.charAt(i);
                if(!validStack.isEmpty()) top = validStack.peek().getKey();
                char curr = s.charAt(i);
                if(validStack.isEmpty() || curr == top || (curr == '(' && top == ')')){
                    Pair p = new Pair(curr,i);
                    validStack.add(p);
                }
                else if(curr == ')' && top == '('){
                    validStack.pop();
                }  
            }
        }
        StringBuilder sb = new StringBuilder(s);
        while(!validStack.isEmpty()){
            int index = validStack.peek().getValue();
            sb.deleteCharAt(index);
            validStack.pop();
        }
        return sb.toString();
    }
}
