                if(!st.empty() && st.top().first=='(')
                    st.pop();
                else st.push({s[i],i});
            }
        }
        if(st.empty()) return s;
        else
        {
            while(!st.empty())
            {
                int idx=st.top().second;
                s.erase(idx,1);
                st.pop();
            }
            return s;
        }
    }
};
