class Solution:
    def minRemoveToMakeValid(self, s: str) -> str:
        out = ''
        stk = []

        for i,c in enumerate(s):
            #characters other than ( and ) to be added to output
            if c not in('(',')'):
                out+=c
            # for ( add it to output and add the index to stack
            elif c == '(':
                out+=c
                stk.append(len(out)-1)
            # for ) check if corresponding ( is there in stack.
            # if yes, then add the ) to output and remove the index from stack 
			# if no, then do not add the ')' as it will make the output invalid
            else:
                if len(stk)>0:
                    stk.pop()
                    out+=c
        # all the unmatched open brackets to be removed from stack as below
        while stk:
            idx = stk.pop()
            out = out[:idx] + out[idx+1:]

        return out

