class Solution {
public:
    string minRemoveToMakeValid(string s) {
        int n=s.length();
        int count=0;
		// first loop from the starting 
        for(int i=0;i<n;i++){
            if(s[i]=='('){
                count++;
            }
            else if(s[i]==')'){
                if(count==0){
                    s[i]='@';
                }
                else{
                    count--;
                }
            }
        }
		// second loop from the ending
        count=0;
        for(int i=n-1;i>=0;i--){
            if(s[i]==')'){
                count++;
            }
            else if(s[i]=='('){
                if(count==0){
                    s[i]='@';
                }
                else{
                    count--;
                }
            }
        }
        string ans="";
        for(int i=0;i<n;i++){
            if(s[i]!='@'){
                ans.push_back(s[i]);
            }
        }
        return ans;
    }
};
