class Solution {
public:
    /**
    Idea:
    1. use a stack stores all left par, if we have a right par, pop stack if stack not empty, or mark it as '*'
    2. mark all redundant left as '*' placeholder, finally remove all '*'
    **/
    string minRemoveToMakeValid(string s) {
        stack<int> stk;
        for(int i=0; i<s.size(); i++){
            if(s[i] == '(') stk.push(i);
            else if(s[i] == ')'){
                if(stk.empty()) s[i] = '*';
                else stk.pop();
            }
        }
        while(!stk.empty()){
            s[stk.top()] = '*';
            stk.pop();
        }
        s.erase(remove(s.begin(),s.end(),'*'), s.end());
        return s;
    }
};
