var minRemoveToMakeValid = function(s) {
    let result = s.split('');
    const indexes = [];
    let balance = 0

    for (let i = 0; i < s.length; i++) {
        if (s[i] === '(') {
            indexes.push(i);
            balance++
        } else if (s[i] === ')') {
            if (balance < 1) indexes.push(i);
            else {
                balance--;
                indexes.pop();
            }
        }
    }

    for (let index of indexes) result[index] = '';

    return result.join('');
};
