        for (int i=0; i<s.size(); i++) {
            if (s[i]=='(' || s[i]==')') {
                if (s[i]=='(') count++, dq.push_back(i);
                else if (count==0 && s[i]==')') dq.push_back(i);
                else if (s[i]==')') count--, dq.pop_back();
