public class Solution {
           public  string MinRemoveToMakeValid(string s)
        {
            Stack<int> stack = new Stack<int>();
            StringBuilder validString = new StringBuilder("");
            int lastIndex = 0;
            for (int i = 0; i < s.Length; i++)
            {
                if (s[i] == ')' && stack.Count == 0)    //removing closing Parentheses (never opened ones)
                    continue;

                if (s[i] == ')')
                    stack.Pop();

                if (s[i] == '(')
                    stack.Push(lastIndex);

                validString.Append(s[i]);
                lastIndex++;
            }

            //removing unclosedBrackets
            if (stack.Count != 0)
            {
                while (stack.Count != 0)
                {
                    validString = validString.Remove(stack.Pop(), 1);
                }
            }

            return validString.ToString();
        }
}
