        //scan from left to right
        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '(')
                balance++;
            else if (s[i] == ')') {
                balance--;
                if (balance < 0) {
                    s[i] = '#';
                    balance = 0;
                }
            }
        }

        balance = 0;
        //scan from right to left
        for (int i = s.length()-1; i >= 0; i--) {
            if (s[i] == ')')
                balance++;
            else if (s[i] == '(') {
                balance--;
                if (balance < 0) {
                    s[i] = '#';
                    balance = 0;                   
                }
            } 
        }

        string result;
        for (auto &ch : s)
            if (ch != '#')
                result += ch;

        return result;
    }
};

