```java
class Solution {
    public String minRemoveToMakeValid(String s) {
        int n = s.length();
        Map<Character, Character> closesMap = new HashMap<>();
        closesMap.put(')', '(');

        Set<Character> opensSet = new HashSet<>();
        opensSet.add('(');

        Stack<CharInString> stack = new Stack<>();
        Set<Integer> toRemove = new HashSet<>();

        for (int i = 0; i < n; ++i) {
            CharInString curr = new CharInString(s.charAt(i), i);
            if (closesMap.containsKey(curr.val)) {
                if (stack.isEmpty())
                    toRemove.add(i);
                else if (stack.peek().val != closesMap.get(curr.val))
                    toRemove.add(i);
                else
                    stack.pop();
            } else if (opensSet.contains(curr.val)) {
                stack.push(curr);
            }
        }

        if ( ! stack.isEmpty() ) {
            while ( ! stack.isEmpty() )
                toRemove.add(stack.pop().pos);
        }

        StringBuilder res = new StringBuilder();
        for (int i = 0; i < n; ++i)
            if ( ! toRemove.contains(i) )
                res.append(s.charAt(i));

        return res.toString();
    }

    private class CharInString {
        char val;
        int pos;

        public CharInString(char val, int pos) {
            this.val = val;
            this.pos = pos;
        }
    }
}
