class Solution {
public:
    string minRemoveToMakeValid(string s) {
        stack<int>st;

        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='(')
            {
                st.push(i);
            }
            else if(s[i]==')')
            {
                if(st.empty())
                {      

                    //STACK ME JO LAST ELEMENT BACH GAYA USKO ! SE DENOTE KIYA HAI 

                    //'!'  IS THE CHARACTER JISKO HUM STRING SE DELETE KARENGE        
                    s[i]='!';           

                }
                else
                {    
                    st.pop();
                }
            }
        }

        while(!st.empty())
        {
            // it means the top of the stack HAS PROBLEMATIC INDEX  AND  WE NEED TO POP IT OUT FROM THE DTACK AS WELL AS THE RESULTANT STRING
            s[st.top()]='!';
            st.pop();
        }

        //remove all '!' from string

        s.erase(remove(s.begin(), s.end(), '!'), s.end());
        return s;

    }
};
