class Solution:
    def minRemoveToMakeValid(self, s: str) -> str:
		# store all the currently open brackets
        stack = []

		# Stores the resulting string as list
        res = []

		# Tracks the index position  of all the open brackets within the resulting list
        index = []
        for c in s:

            if c == ')' and len(stack) > 0 and stack[-1] == "(":
                res.append(c)
                stack.pop()
            elif c == '(':
                index.append(len(res))
                res.append(c)
                stack.append(c)
            elif c not in ['(', ')']:
                res.append(c)

        while len(stack) > 0:
            res[index.pop()] = ""
            stack.pop()

        return "".join(res)


