// O(N) | O(N)
func minRemoveToMakeValid(s string) string {
    count := 0
    popingLeft := make([]rune, 0)
    // Poping ")"
    for _, char := range(s){
        if char == ')'{
            if count == 0{
                continue
            } else {
                count -= 1
            }
        } else if char == '('{
            count += 1
        }
        popingLeft = append(popingLeft, char)
    }


    // Poping "(" if count >0 else return string(popingLeft)
    if count > 0{
        count = 0
        res := make([]rune, 0)
        for i := len(popingLeft) - 1; i > -1; i--{
            if popingLeft[i] == '('{
                if count == 0{
                    continue
                } else {
                    count -=1
                }
            } else if popingLeft[i] == ')'{
                count += 1
            }
            res = append(res, popingLeft[i])
        }
        // Reverse res
        for i, j := 0, len(res)-1; i < j; i, j = i+1, j-1 {
            res[i], res[j] = res[j], res[i]
        }
        return string(res)
    }else{

        return string(popingLeft)
    }
}
