class Solution {
public:
    string minRemoveToMakeValid(string s) {
        int n = s.size();
        vector<char> st(n);
        int count = 0;
		// we are storing each character of the given string in a vector
        for(int i=0;i<n;i++)
        {
            st[i] = s[i];
        }
		// we are checking that every opening bracket have closing bracket or not from left to right, if it not having closing bracket we will make that particular character = 0   
        for(int i=0;i<st.size();i++)
        {
            if(st[i] == '(')
                count++;
             else if(st[i] == ')')
             {
                 if(count>0) count--;
                 else
                 {
                     st[i] = 0;
                 }
             }
        }
        count = 0;
			// we are checking that every closing bracket have opening bracket or not from right to left, if it not having opening bracket we will make that particular character = 0   
        for(int i=st.size()-1;i>=0;i--)
        {
            if(st[i] == ')')
                count++;
             else if(st[i] == '(')
             {
                 if(count>0) count--;
                 else
                 {
                     st[i] = 0;
                 }
             }
        }
        string res;
		/// now we can append that result in the string 
        for(int i=0;i<st.size();i++)
        {
            if(st[i]!=0)
                res+=st[i];
        }
        return res;
    }
};
