            else if(ch == ')') {
                close++;
            }
            else {
                if(close > open) {
                    open++;
                }
                else {
                    A[i] = '*';
                    close = close < 0 ? 0 : close--;
                }
            }
        }

        for(char ch : A) 
            if(ch!='*')
                R.append(ch);

        return R.toString();
