class Solution {
    public String minRemoveToMakeValid(String s) {
        char[] sArray = s.toCharArray();
        Stack<Integer> stack = new Stack<>();
        for (int i = 0; i < sArray.length; i++) {
            if (sArray[i] == '(') stack.push(i);
            else if (sArray[i] == ')') {
                if (stack.isEmpty()) sArray[i] = '0';
                else stack.pop();
            }
        }
        while (!stack.isEmpty()) sArray[stack.pop()] = '0';
        StringBuilder sb = new StringBuilder();
        for (char c : sArray) {
            if (c != '0') sb.append(c);
        }
        return sb.toString();
    }
}
