class Solution:
    def minRemoveToMakeValid(self, s: str) -> str:
        invalids = set()
        stack = []
        for idx, ch in enumerate(s):
            if ch == '(':
                stack.append(idx)
            elif ch == ')':
                if not stack: # no prior opening parenthesis
                    invalids.add(idx)
                else:
                    stack.pop()

        for idx in stack:  # if stack is non empty (unclosed parenthesis)
            invalids.add(idx)

        ans = []
        for i in range(len(s)):
            if i in invalids:
                continue
            ans.append(s[i])

        return "".join(ans)
