class Solution:
    def minRemoveToMakeValid(self, s: str) -> str:
        stackParan = []
        stackIdx = []
        n = len(s)
        for i in range(n):
            if s[i].islower() == True:
                continue
            else:
			#Checking Valid Paranthesis
                if len(stackParan) == 0 and len(stackIdx) == 0:
                    stackParan.append(s[i])
                    stackIdx.append(i)
                else:
                    if stackParan[-1] == "(" and s[i] == ")":
                        stackParan.pop()
                        stackIdx.pop()
                    elif stackParan[-1] == ")" or stackParan[-1] == "(":
                        stackParan.append(s[i])
                        stackIdx.append(i)

        s = list(s)
        while stackIdx != []:
            s.pop(stackIdx[-1])
            stackIdx.pop()

        return "".join(s)
