class Solution {
    public int stoneGameVI(int[] a, int[] b) {
        int n = a.length;
		pair p[] = new pair[n];
		for (int i = 0; i < n; i++) 
			p[i] = new pair(a[i] + b[i], i); 

        Arrays.sort(p, (x,y) -> y.v - x.v); // sorting in decreasing order (a[i] + b[i])

        int av = 0, bv = 0;
		for(int i=0;i<n ;i++){
            if(i%2 == 0){
                av += a[p[i].i];
            }else{
                bv += b[p[i].i];
            }
        }
		return av == bv ? 0 : (av > bv ? 1 : -1);
	}

	class pair {
		int v, i;
		pair(int dd, int ii) {
			v = dd;
			i = ii;
		}
	}
}
```

