class Solution {
    public int stoneGameVI(int[] aliceValues, int[] bobValues) {
        int alice=0,bob=0,n=aliceValues.length;
        int stones[][]=new int [n][3];
        for(int i=0;i<n;i++){
            stones[i]=new int[]{i,aliceValues[i],bobValues[i]};
        }
        Arrays.sort(stones,(a,b)->(b[1]+b[2])-(a[1]+a[2]));
        for(int i=0;i<n;i++){
            int []stone=stones[i];
            if(i%2==0){
                alice+=stone[1];
            }
            else{
                bob+=stone[2];
            }
        }
        if(alice==bob)return 0;
        return alice>bob?1:-1;
    }
}
