        for (int i = 0; i < n; i++) {
            pairs[i] = new Pair(aliceValues[i],bobValues[i]);
        }
        Arrays.sort(pairs,new Comparator<Pair>() {
            public int compare(Pair p1,Pair p2) {
                return Integer.compare(p1.a + p1.b,p2.a + p2.b);
            }
        });
        int sum1 = 0;
        int sum2 = 0;
        int turn = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (turn == 0) {
                sum1 += pairs[i].a;
            } else {
                sum2 += pairs[i].b;
            }
            turn = 1 - turn;
        }
        return Integer.compare(sum1,sum2);
    }
}
