        for (int i = a.length-1; i >= 0; i--)
        {
            if (alice)
            {
                sum += a[value[i][0]];
                alice = false;
            }
            else
            {
                sum -= b[value[i][0]];
                alice = true;
            }
        }
        return (sum > 0) ? 1 : (sum < 0) ? -1 : 0;
    }
}

