  // Complexity: Linearithmic in the length of aliceValues
  int stoneGameVI(vector<int>& aliceValues, vector<int>& bobValues) {
    vector<int> stones;
    int score = 0, n = aliceValues.size();
    for (int i = 0; i < n; i++) stones.push_back(i);

    // Sort stones in descending order of the sum of evaluations of Alice and 
    // Bob. We are doing this because the real value of a stone is the sum
    // of its value for Alice and its value for Bob. If Alice takes stone S,
    // she does not only gain her valuation of it, but she also hinders Bob from
    // gaining his valuation of S.
    sort(begin(stones), end(stones), [&aliceValues, &bobValues](int i, int j){
      return aliceValues[i] + bobValues[i] > aliceValues[j] + bobValues[j];
    });

    // Alice makes the first move thus she is the even positions.
    for (int i = 0; i < n; i++) {
      if (i & 1) score -= bobValues[stones[i]];
      else score += aliceValues[stones[i]];
    }
    return score != 0? score > 0? 1 : -1 : 0;
  }
