class Solution {
public:
    int stoneGameVI(vector<int>& aliceValues, vector<int>& bobValues) {
        priority_queue<pair<int,int>> pq;
        int i, j=0, sum1=0, sum2=0;
        for(i=0 ; i<aliceValues.size() ; i++)
