typedef vector< vector<pair<int, int>> >vv;
typedef vector<pair<int, int>>vi;
class Solution {
public:

    int stoneGameVI(vector<int>& Alice, vector<int>& Bob) {
    int n=Bob.size();
    priority_queue<pair<int, int>>Pq;
    for (int x = 0; x < n; x++)Pq.push({Alice[x]+Bob[x],x});
    int A = 0, B = 0,Ch=1;
    while (!Pq.empty())
     {
    pair<int, int>cur = Pq.top();
    if (Ch)A += (cur.first - Bob[cur.second]);
    else B += (cur.first - Alice[cur.second]);
    Ch = !Ch;
    Pq.pop();
    }
    return (A > B ? 1 : (A == B ? 0 : -1));
    }
};
```

