class Solution {
public:
    static bool comp(pair<int,int>&a,pair<int,int>&b){
        return a.first>b.first;
    }
    pair<int,int>helper(vector<int>&a,vector<int>&b,int sum1,int sum2){
        int n= a.size();
        vector<pair<int,int>>temp;
        for(int i=0; i<n; i++){
            int sum= a[i]+b[i];
            temp.push_back({sum,i});

        }
        sort(temp.begin(),temp.end(),comp);
        bool flag=true;
        for(int i=0; i<n; i++){
            if(flag){
                sum1+= a[temp[i].second];
            }
            else if(!flag){
                sum2+= b[temp[i].second];
            }
            flag= !flag;

        }
        return {sum1,sum2};

    }
    int stoneGameVI(vector<int>& a, vector<int>& b) {
      pair<int,int>temp=  helper(a,b,0,0);
        if(temp.first>temp.second) return 1;
        else if(temp.second==temp.first) return 0;
        else{
            return -1;
        }



    }
};

