class Solution {
public:
    int stoneGameVI(vector<int>& aliceValues, vector<int>& bobValues) {

        priority_queue<pair<int,pair<int,int>>>pq;
        int alice = 0;
        int bob = 0;

        for(int i = 0;i<aliceValues.size();i++){

            int s = aliceValues[i] + bobValues[i];
            pq.push({s,{aliceValues[i],bobValues[i]}});
        }

        int c = 0;
        while(!pq.empty()){

            if(c%2==0){
                alice = alice + pq.top().second.first;
            }
            else{
                bob = bob + pq.top().second.second;
            }
            c++;
            pq.pop();
        }
        if(alice>bob)return 1;
        if(bob>alice)return -1;
        return 0;

    }
};
