import heapq

class Solution:
    def stoneGameVI(self, aliceValues: List[int], bobValues: List[int]) -> int:
        """
        When one takes the stone, 
        they not only get the points, 
        but they take them away from the other player too.
        Greedily choose the stone with the 
        maximum aliceValues[i] + bobValues[i]
        """
        heap, ALICE, BOB = [], 0, 1
        for i, a_v, b_v in zip(range(len(aliceValues)), aliceValues, bobValues):
            heapq.heappush(heap, (-(a_v+b_v), i))
        alice, bob, turn = 0, 0, 0
        while len(heap):
            _, idx = heapq.heappop(heap)
            if turn == ALICE:
                alice+=aliceValues[idx]
                turn = BOB
            else:
                bob+=bobValues[idx]
                turn = ALICE
        if alice > bob:
            return 1
        elif bob > alice:
            return -1
        else:
            return 0

