func stoneGameVI(aliceValues []int, bobValues []int) int {
	n := len(aliceValues)

	aliceHeap := HeapStone{}
	bobHeap := HeapStone{}

	for i := 0; i < n; i++ {
		heap.Push(&aliceHeap, Stone{index: i, value: aliceValues[i], other: bobValues[i]})
		heap.Push(&bobHeap, Stone{index: i, value: bobValues[i], other: aliceValues[i]})
	}

	takenStone := make([]bool, n)
	aliceScore := 0
	bobScore := 0

    for i := 0; i < n; i++ {
        if i % 2 == 0 {
            if takenStone[aliceHeap[0].index] == true {
                heap.Pop(&aliceHeap)
            }

            aliceStone := heap.Pop(&aliceHeap).(Stone)

            // fmt.Printf("alice choose stone %+v\n", aliceStone)

            takenStone[aliceStone.index] = true
            aliceScore += aliceStone.value
        } else {
            for takenStone[bobHeap[0].index] == true {
                heap.Pop(&bobHeap)
            }

            bobStone := heap.Pop(&bobHeap).(Stone)

            // fmt.Printf("bob choose stone %+v\n", bobStone)

            takenStone[bobStone.index] = true
            bobScore += bobStone.value
        }
    }

	// fmt.Printf("aliceScore = %d, bobScore = %d\n", aliceScore, bobScore)

	if aliceScore > bobScore {
		return 1
	}

	if aliceScore < bobScore {
		return -1
	}

	return 0
}

type Stone struct {
	index int
	value int
	other int
}

type HeapStone []Stone

func (h HeapStone) Len() int           { return len(h) }
func (h HeapStone) Less(i, j int) bool { return h[i].value+h[i].other > h[j].value+h[j].other }
func (h HeapStone) Swap(i, j int)      { h[i], h[j] = h[j], h[i] }

func (h *HeapStone) Push(x interface{}) {
	*h = append(*h, x.(Stone))
}

func (h *HeapStone) Pop() interface{} {
	old := *h
	n := len(old)
	x := old[n-1]
	*h = old[0 : n-1]
	return x
}
