import java.util.SortedMap;
class Solution {
    public int stoneGameVI(int[] aliceValues, int[] bobValues) {
        SortedMap<Integer,List<List<Integer>>> hashMap = new TreeMap();
        for(int i = 0; i < aliceValues.length; i++){
            List<Integer> temp = new ArrayList();
            temp.add(aliceValues[i]);
            temp.add(bobValues[i]);
            if(hashMap.get(aliceValues[i] + bobValues[i]) != null){
                hashMap.get(aliceValues[i] + bobValues[i]).add(temp);
                continue;
            } else{
                hashMap.put(aliceValues[i] + bobValues[i], new ArrayList());
            }
            hashMap.get(aliceValues[i] + bobValues[i]).add(temp);
        }
        int index = 0;
        int a = 0;
        int b =0;
        Stack<Integer> stack = new Stack();
        for(Map.Entry<Integer,List<List<Integer>>> entry: hashMap.entrySet()){
            stack.push(entry.getKey());
        }
        while(!stack.isEmpty()){
            int key = stack.peek();
            List<List<Integer>> tList = hashMap.get(key);
            if( index % 2 == 0){
            a = a + tList.get(0).get(0);

            tList.remove(0);
            } else{

            b = b + tList.get(tList.size()-1).get(1);

            tList.remove(tList.size()-1);

            }
            if(tList == null || tList.isEmpty()){
                stack.pop();
            }
            index++;
        }
        if(a>b){
            return 1;
        } else if(b>a){
            return -1;
        } else{
            return 0;
        }
    }
}
