class Solution {
    class Pair implements Comparable<Pair>
    {
        int index;
        int sum;
        public Pair(int n,int v)
        {
            index=n;
            sum=v;
        }
        public int compareTo(Pair p)
        {
            if(p.sum>sum)
                return 1;
            else
                return -1;
        }
    }
    public int stoneGameVI(int[] aliceValues, int[] bobValues) {
        PriorityQueue<Pair> pq=new PriorityQueue<>();
        for(int i=0;i<aliceValues.length;i++)
        {
            pq.add(new Pair(i,aliceValues[i]+bobValues[i]));
        }
        int flag=0,sum=0;
        while(!pq.isEmpty())
        {
            Pair p=pq.poll();
            if(flag%2==0)
            {
                sum+=p.sum-bobValues[p.index];
                flag++;
            }
            else
            {
                sum-=(p.sum-aliceValues[p.index]);
                flag++;
            }
        }
        if(sum==0)
            return 0;
        else if(sum>0)
            return 1;
        else
            return -1;
    }
}
