impl Solution {
    pub fn stone_game_vi(alice_values: Vec<i32>, bob_values: Vec<i32>) -> i32 {
        let n = alice_values.len();
        let mut memo = vec![(0,0);n];
        for i in 0..n {
            memo[i] = (alice_values[i] + bob_values[i], i);
        }
        memo.sort();
        memo.reverse();

        let mut a = 0;
        let mut b = 0;
        for i in 0..n {
            if i % 2 == 0 {
                a += alice_values[memo[i].1];
            } else {
                b += bob_values[memo[i].1];
            }
        }

        if a > b {
            1
        } else if a == b {
            0
        } else {
            -1
        }
    }
}
