var stoneGameVI = function (aliceValues, bobValues) {
  let pick_me_first = new Array(aliceValues.length).fill([]).map((_, idx, arr) => (arr[idx] = [aliceValues[idx], bobValues[idx]])).sort((a, b) => b[0] + b[1] - (a[0] + a[1]));
  let diff = pick_me_first.reduce((acc, val, idx) => (acc += idx % 2 === 0 ? val[0] : 0), 0) - pick_me_first.reduce((acc, val, idx) => (acc += idx % 2 === 1 ? val[1] : 0), 0);
  return diff > 0 ? 1 : diff < 0 ? -1 : diff;
};
