class Solution {
public:
    int stoneGameVI(vector<int>& A, vector<int>& B) {
        int n=A.size();
        priority_queue<tuple<int,int,int>> pq;
        for(int i=0;i<n;i++){
            pq.push({A[i]+B[i],A[i],B[i]});
        }
        int res=0;
        bool ok=true;
        while(!pq.empty()){
            auto curr=pq.top();pq.pop();
            if(ok){
                res+=get<1>(curr);
                ok=!ok;
            }
            else{
                res-=get<2>(curr);
                ok=!ok;
            }
        }
        if(res>0) return 1;
        else if(res<0) return -1;
        return 0;
    }
};

