public class Solution {
    public int StoneGameVI(int[] aliceValues, int[] bobValues) {
        var moves = new int[aliceValues.Length][];

        for(var i = 0; i < aliceValues.Length; i++)
        {
            moves[i] = new []{ aliceValues[i] + bobValues[i], i};
        }

        Array.Sort(moves, (x,y) => y[0].CompareTo(x[0]));

        var alice = 0;
        var bob = 0;

        for(var i = 0 ; i < moves.Length; i++)
        {
            if(i % 2 == 0)
            {
                alice += aliceValues[moves[i][1]];
            }
            else
            {
                bob += bobValues[moves[i][1]];
            }
        }

        return alice == bob ? 0 : alice > bob ? 1 : -1;
    }
}
