class Pair {
    int sum, a, b;
    Pair(int a, int b) {
        sum = a + b;
        this.a = a;
        this.b = b;
    }
}

class PairComparator implements Comparator<Pair> {
    @Override
    public int compare(Pair p1, Pair p2) {
        return p2.sum - p1.sum;
    }
}

class Solution {
    public int stoneGameVI(int[] aliceValues, int[] bobValues) {
        PriorityQueue<Pair> maxHeap = new PriorityQueue<>(new PairComparator());
        for(int i=0;i<aliceValues.length;i++) {
            maxHeap.offer(new Pair(aliceValues[i], bobValues[i]));
        }
        boolean turn = true;
        int aliceScore = 0, bobScore = 0;
        while(!maxHeap.isEmpty()) {
            if(turn) {
                aliceScore += maxHeap.poll().a;
            } else {
                bobScore += maxHeap.poll().b;
            }
            turn = !turn;
        }
        return Integer.compare(aliceScore, bobScore);
    }
}
