import scala.collection.immutable.SortedSet
import scala.util.chaining.scalaUtilChainingOps

object Solution {
  private object Player extends Enumeration {
    type Player = Value
    val Alice, Bob = Value
  }
  import Player._

  def stoneGameVI(aliceValues: Array[Int], bobValues: Array[Int]): Int = aliceValues
    .view
    .zip(bobValues)
    .zipWithIndex
    .map { case ((aliceValue, bobValue), index) => Stone(index, aliceValue, bobValue) }
    .to(SortedSet)
    .foldLeft(Alice, 0, 0) {
      case ((Alice, aliceScore, bobScore), Stone(_, aliceValue, _)) => (Bob, aliceScore + aliceValue, bobScore)
      case ((_, aliceScore, bobScore), Stone(_, _, bobValue)) => (Alice, aliceScore, bobScore + bobValue)
    }
    .pipe { case (_, aliceScore, bobScore) => math.signum(aliceScore.compareTo(bobScore)) }

