```class Solution {
    public int stoneGameVI(int[] aliceValues, int[] bobValues) {
        LinkedList[] pointsDifference = (LinkedList[]) new LinkedList[201];

        int maxVal = 0;
        for (int i=0; i<aliceValues.length;i++) {
            int weight = aliceValues[i]+bobValues[i];
            if (weight > maxVal) {
                maxVal=weight;
            }
            LinkedList<Integer> existingDiff=pointsDifference[weight];
            if (existingDiff == null) {
                existingDiff = new LinkedList<Integer>();
                pointsDifference[weight]=existingDiff;
            }
            existingDiff.add(i);
        }

        int bestPlay = maxVal;
        int currentPlay = 0;
        int bobPoints = 0;
        int alicePoints = 0;
        boolean alicePlays=true;
        while (currentPlay<aliceValues.length) {
            if (alicePlays == true) {
                LinkedList<Integer> plays = pointsDifference[bestPlay];
                if (plays != null && plays.size()>0) {
                    currentPlay += 1;
                    alicePoints += aliceValues[plays.poll()];
                    alicePlays = false;
                } else {
                    bestPlay = bestPlay-1;
                }
            } else {
                LinkedList<Integer> plays = pointsDifference[bestPlay];
                if (plays != null && plays.size()>0) {
                    currentPlay += 1;
                    bobPoints += bobValues[plays.poll()];
                    alicePlays = true;
                } else {
                    bestPlay = bestPlay-1;
                }
            }
        }


        if (alicePoints > bobPoints ) {
            return 1;
        } else if ( alicePoints < bobPoints) {
            return -1;
        } else {
            return 0;
        }
    }
}
