object Solution {
  def stoneGameVI(aliceValues: Array[Int], bobValues: Array[Int]): Int = {
    lazy val x = (aliceValues zip bobValues)
    .sortBy (ab => - ab._1 - ab._2)          // descending sort by sum a+b
    .map(ab => Seq(ab._1, - ab._2))          // positive moves for A, negative for B
    .zip(Stream.continually(0 to 1).flatten) // zip with 0,1,0,1,0,1
    .map(ab=>ab._1(ab._2)).sum               // sum a(0),b(1),a(2),b(3)...
    if(x==0) 0 else if(x>0) 1 else -1        // get sign
  }
}
