//  Key idea behind this approach is to spirally(clockwise) traverse matrix and expand traversal area( decrementing top and left, incrementing right and down) and keep checking boundary condition

// T.C = O(rows*cols) , S.C = O(rows*cols)

class Solution {
public:
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rStart, int cStart) {


        vector<vector<int>> ans ;

        // keep track which cell is visited to avoid duplicate insertion in ans 
        vector<vector<bool>> isvisited( rows, vector<bool>( cols, false ) ) ;

        int top = rStart , down = (rStart < (rows-1)) ? rStart+1 : rStart , left = cStart, right = (cStart < (cols-1)) ? cStart+1 : cStart ;

        int direction = 0 ;   // 0 == forward, 1 == downward , 2 == backward, 3 == upward


        ans.push_back({rStart, cStart}) ;

        isvisited[rStart][cStart] = true ;

        while( (int)ans.size() < (rows*cols) ){


            // forward move

            if( direction == 0 ){

                for( int i = left+1 ; i <= right ; ++i ){

                    if(!isvisited[top][i]){

                        ans.push_back({ top , i }) ;

                        isvisited[top][i] = true ;
                    }
                }
            }

            // downward move

            else if( direction == 1 ){

                for( int i = top+1 ; i <= down ; ++i ){

                    if( !isvisited[i][right] ){

                        ans.push_back({ i , right }) ;

                        isvisited[i][right] = true ;
                    }
                }
                // if we can expand area, expand it(boundary condtion)
               if( top > 0 ){
                	top -= 1 ;
                }
                if( left > 0 ){
                	left -= 1 ;
                }
            }

            // backward move

            else if( direction == 2 ){

                for( int i = right-1 ; i >= left ; --i ){

                    if( !isvisited[down][i] ){

                        ans.push_back({ down , i }) ;

                        isvisited[down][i] = true ;
                    }
                }
                // if we can expand area, expand it(boundary condtion)
                if( right < (cols-1)){
                	right += 1 ;
                }
            }

            // upward move

            else if( direction == 3 ){

                for( int i = down-1 ; i >= top ; --i ){

                    if( !isvisited[i][left] ){

                        ans.push_back({ i, left }) ;

                        isvisited[i][left] = true ;
                    }
                }
                // if we can expand area, expand it(boundary condtion)
                if( down < (rows-1)){
                	down += 1;
                }
            }


            direction =  (direction+1)%4 ;

        } 

        return ans ;

    }
};

