            //Check out of bound condition , if it is out of bound , don't do anything
            //just return it

            if(r>=rows || r<0 || c>=cols || c<0) {
                return;
            }

             mat[size][0] = r;
             mat[size++][1] = c;        //size is increaed for next trip or iteration
