class Solution {
    public int[][] spiralMatrixIII(int R, int C, int r0, int c0) {
        int[][] result = new int[R*C][2];

        int direction=0;//{0,1,2,3};
        int index=0;
        int r=r0, c=c0;
        int step=0, size=1;

        while(index < R*C){
            if(r>=0 && r<=R-1 && c>=0 && c<=C-1){
                result[index][0]=r;
                result[index][1]=c;
                index++;
            }

            if(direction==0){
                c++;
            }else if(direction==1)
                r++;
            else if(direction==2){
                c--;
            }else if(direction==3)
                r--;

            step++; //steps taken on a same direction
            if(step == size){
                direction= (direction+1) %4;
                //only on east and west we increase size.
                if(direction==0 || direction ==2)
                    size++;
                step=0;
            }
        }
        return result;
    }
}
