class Solution {

    int[] dr = new int[] { 0, 1, 0, -1 };
    int[] dc = new int[] { 1, 0, -1, 0 };

    public int[][] spiralMatrixIII(int R, int C, int r0, int c0) {

        int x = r0, y = c0;

        int[][] res = new int[R*C][2];
        res[0][0] = r0;
        res[0][1] = c0;

        int count = 1;
        int size = 1;

        int dir = 0;

        while (count < R * C) {

            for (int i = 0; i < size; i++) {
                x = x + dr[dir];
                y = y + dc[dir];

                if (0 <= x && x < R && 0 <= y && y < C) {
                    res[count][0] = x;
                    res[count][1] = y;
                    count++;
                }
            }

            dir = (dir + 1) % 4;;

            for (int i = 0; i < size; i++) {
                x = x + dr[dir];
                y = y + dc[dir];

                if (0 <= x && x < R && 0 <= y && y < C) {
                    res[count][0] = x;
                    res[count][1] = y;
                    count++;
                }
            }

            dir = (dir + 1) % 4;
            size++;
        }

        return res;
    }
}
