/**
 * @param {number} R
 * @param {number} C
 * @param {number} r0
 * @param {number} c0
 * @return {number[][]}
 */
var spiralMatrixIII = function(R, C, r0, c0) {
    // Set start space.
    let r = r0;
    let c = c0;
    let startSpace = [r, c];

    // Define possible diredctions, start from index = 0.
    const directions = ['+c', '+r', '-c', '-r'];
    let directionIndex = 0;

    // Add startSpace to the result array.
    const result = [startSpace];

    // Mark the start space as parsed and start from spiral shape with length = 2.
    let parsedSpaces = 1;
    let spiralLength = 2;

    // Define helper method for setting the next direction.
    const changeDirection = () => {
        directionIndex = directionIndex + 1 > 3 ? 0 : directionIndex + 1;
    }

    // Iterate until all R * C spaces of the grid are parsed.
    while (result.length < R * C) {
        // Calculate the amount of spaces in the next spiral shape.
        const areaOfSpiralShape = spiralLength ** 2;

        // Iterate until all spaces of the current spiral shape are parsed.
        while (parsedSpaces <= areaOfSpiralShape) {
            const isLastSpace = parsedSpaces === areaOfSpiralShape - 1;

            // 1. Go to the next space by incrementing/decrementing c/r according to direction.
            // 2. Check if a wall of the current spiral shape is reached.
            // 3. Change direction if the wall is reached.
            // 4. Exception: leave the current direction if the last space of spiral is reached.
            switch (directions[directionIndex]) {
                case '+c':
                    c++;
                    if (!isLastSpace && c + 1 >= startSpace[1] + spiralLength) {
                        changeDirection();
                    }
                    break;
                case '-c':
                    c--;
                    if (!isLastSpace && c - 1 <= startSpace[1] - spiralLength) {
                        changeDirection();
                    }
                    break;
                case '+r':
                    r++;
                    if (!isLastSpace && r + 1 >= startSpace[0] + spiralLength) {
                        changeDirection();
                    }
                    break;
                case '-r': 
                    r--;
                    if (!isLastSpace && r - 1 <= startSpace[0] - spiralLength) {
                        changeDirection();
                    }
                    break;
            }

            // Add to result in case [r, c] is within R*C grid.
            if (c >= 0 && c < C && r >= 0 && r < R) {
                result.push([r, c]);
            }
            parsedSpaces++;
        }

        // Set the start space of the next spiral.
        startSpace = [r, c];
        spiralLength++;
    }

    return result;
};
