class Solution:
    def doMove(self, rows, cols, current_pos, direction_index, distance):
        directions = ['e', 's', 'w', 'n']
        new_direction = directions[direction_index]
        spaces_moved = 1
        moves = []
        while(spaces_moved <= distance):
            if new_direction == 'e':
                if current_pos[1] + 1 < cols and current_pos[0] >= 0 and current_pos[0] < rows and current_pos[1]+1 >=0:
                    moves.append([current_pos[0], current_pos[1]+1])
                current_pos = [current_pos[0], current_pos[1]+1]


            elif new_direction == 's':
                if current_pos[0]+1 < rows and current_pos[1] < cols and current_pos[1] >= 0 and current_pos[0]+1 >=0:
                    moves.append([current_pos[0]+1, current_pos[1]])
                current_pos = [current_pos[0]+1, current_pos[1]]

            elif new_direction == 'w':
                if current_pos[1] - 1 >= 0 and current_pos[0] >=0 and current_pos[0] < rows and current_pos[1]-1 < cols:
                    moves.append([current_pos[0], current_pos[1] - 1])
                current_pos = [current_pos[0], current_pos[1] - 1]

            else:
                if current_pos[0] - 1 >= 0 and current_pos[1] < cols and current_pos[1] >= 0 and current_pos[0] - 1 < rows:
                    moves.append([current_pos[0]-1, current_pos[1]])
                current_pos = [current_pos[0]-1, current_pos[1]]

            spaces_moved += 1

        return moves, current_pos



    def spiralMatrixIII(self, rows: int, cols: int, rStart: int, cStart: int) -> List[List[int]]:
        skip_add = True
        visited = []
        direction_index = 0
        distance = 1
        current_pos = [rStart, cStart]
        visited.append(current_pos)

        while(len(visited) < (rows * cols)):
            latest_moves, current_pos = self.doMove(rows, cols, current_pos, direction_index, distance)
            visited.extend(latest_moves)
            direction_index += 1
            if direction_index > 3:
                direction_index = 0
            if not skip_add:
                skip_add = True
                distance += 1
            else:
                skip_add = False



        return visited
