class Solution {
    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        int totalCells = rows * cols;
        int[][] result = new int[totalCells][2];

        result[0][0] = rStart;
        result[0][1] = cStart;
        if (totalCells == 1) return result;

        // every left and every right up the steps;
        int totalSteps = 1;
        int steps = 0;
        String dir = "right";
        int[] currentCell = new int[]{rStart, cStart, 0};
        while (totalSteps < totalCells) {
            if (dir.equals("right")) {
                steps++;
                totalSteps += addSteps(dir, steps, currentCell, result, rows, cols);
                dir = "down";
            }
            if (dir.equals("down")) {
                totalSteps += addSteps(dir, steps, currentCell, result, rows, cols);
                dir = "left";
            }
            if (dir.equals("left")) {
                steps++;
                totalSteps += addSteps(dir, steps, currentCell, result, rows, cols);
                dir = "up";
            }
            if (dir.equals("up")) {
                totalSteps += addSteps(dir, steps, currentCell, result, rows, cols);
                dir = "right";
            }
        }
        return result;
    }

    private int addSteps(String dir, int steps, int[] currentCell, int[][] result, int rows, int cols) {
        int addedSteps = 0;
        for (int i = 0; i < steps; i++) {
            if (dir.equals("right")) {
                currentCell[1]++;
            } else if (dir.equals("down")) {
                currentCell[0]++;
            } else if (dir.equals("left")) {
                currentCell[1]--;
            } else  if (dir.equals("up")) {
                currentCell[0]--;
            }
            if (currentCell[0] < 0 || currentCell[0] >= rows || currentCell[1] < 0 || currentCell[1] >= cols) continue;
            currentCell[2]++;
            result[currentCell[2]][0] = currentCell[0];
            result[currentCell[2]][1] = currentCell[1];
            addedSteps++;
        }
        return addedSteps;
    }
}
