class Solution {
    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        // lets make some boundaries and stick with it !
        int left = cStart -1 , right = cStart+1 , top = rStart-1 , btm = rStart+1 , cnt= 0;
        int [][] res = new int[rows*cols][2];
        while(cnt < rows * cols ){
            // move left to right
            for( ; cStart <= right ; cStart++){
                if( check(rStart , cStart , rows , cols)) res[cnt++] = new int[]{rStart ,cStart};
            }
            cStart = right ;
            rStart++;

            // top to btm 
            for( ; rStart <= btm ; rStart++){
                if( check(rStart , cStart , rows , cols)) res[cnt++] = new int[]{rStart ,cStart};
            }
            rStart = btm  ;
            cStart--;

            //right to left
            for( ; cStart >= left ; cStart--){
                if( check(rStart , cStart , rows , cols)) res[cnt++] = new int[]{rStart ,cStart};
            }
            cStart = left;
            rStart--;

            // btm to top
            for( ; rStart >= top ; rStart--){
                if( check(rStart , cStart , rows , cols))   res[cnt++] = new int[]{rStart ,cStart};
            }
            rStart = top ;
            cStart++;

            top--;btm++;left--;right++;

        }
        return res;
    }
    public boolean check( int rStart , int cStart , int rows , int cols){
        if( rStart >= 0 && rStart <rows && cStart >= 0 && cStart < cols)  return true ;
        return false ;
    }
}
