# Code
```
class Solution {
    public int[][] spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        int size = 1;
        int dir = 1;
        int result[][] = new int[rows * cols][2];
        result[0] = new int[]{rStart, cStart};

        while (size < rows * cols) {
            // for east
            int east = 1;
            while (east <= dir) {
                cStart++;
                if (check(rStart, cStart, rows, cols)) {
                    result[size] = new int[]{rStart, cStart};
                    size++;
                }
                east++;
            }

            // for south
            int south = 1;
            while (south <= dir) {
                rStart++;
                if (check(rStart, cStart, rows, cols)) {
                    result[size] = new int[]{rStart, cStart};
                    size++;
                }
                south++;
            }

            dir++;

            // for west
            int west = 1;
            while (west <= dir) {
                cStart--;
                if (check(rStart, cStart, rows, cols)) {
                    result[size] = new int[]{rStart, cStart};
                    size++;
                }
                west++;
            }

            // for north
            int north = 1;
            while (north <= dir) {
                rStart--;
                if (check(rStart, cStart, rows, cols)) {
                    result[size] = new int[]{rStart, cStart};
                    size++;
                }
                north++;
            }

            dir++;
        }

        return result;
    }

    public static boolean check(int row, int col, int rows, int cols) {
        return row >= 0 && row < rows && col >= 0 && col < cols;
    }
}

