class Solution {
public:
    vector<vector<int>> spiralMatrixIII(int rows, int cols, int rStart, int cStart) {
        vector<vector<int>> ans;
        ans.reserve(rows*cols);

        enum directions {RIGHT, DOWN, LEFT, UP};
        int dir = RIGHT;
        int step = 1;
        int countAll = rows*cols;

        int i = rStart;
        int j = cStart;

        while(countAll > 0) {
            if (dir == RIGHT) {
                if (i >=0 && i < rows)
                for(int k = max(j, 0), maxK = min(j+step-1, cols-1); k <= maxK; k++) {
                    ans.push_back({i, k});
                    countAll--;
                }
                j += step;
                dir = DOWN;
            } else if (dir == DOWN) {
                if (j >=0 && j < cols)
                for(int k = max(i, 0), maxK = min(i+step-1, rows-1); k <= maxK; k++) {
                    ans.push_back({k, j});
                    countAll--;
                }
                i += step;
                step++;
                dir = LEFT;
            }if (dir == LEFT) {
                if (i >=0 && i < rows)
                for(int k = min(j, cols-1), minK = max(j-step+1, 0); k >= minK; k--) {
                    ans.push_back({i, k});
                    countAll--;
                }
                j -= step;
                dir = UP;
            } else if (dir == UP) {
                if (j >=0 && j < cols)
                for(int k = min(i, rows-1), minK = max(i-step+1, 0); k >= minK; k--) {
                    ans.push_back({k, j});
                    countAll--;
                }
                i -= step;
                step++;
                dir = RIGHT;
            }
        }
        return ans;
    }
};
